package mirroruniverse.g1;

public class Node {	
	int x;
	int y;
	Node next ;
	
	public Node (int x, int y){
		this.x = x;
		this.y = y;
	}
	
	public Node() {
		// TODO Auto-generated constructor stub
	}

	public int getX() {
		return x;
	}
	public void setX(int x) {
		this.x = x;
	}
	public int getY() {
		return y;
	}
	public void setY(int y) {
		this.y = y;
	}
	
	
}
